('LOC_PEDIA_CONCEPTS_PAGEGROUP_FALLFROMHEAVEN_NAME', 'Fall from Heaven Concepts', 'en_US'),
('LOC_PEDIA_CONCEPTS_PAGE_AFFINITY_CHAPTER_CONTENT_TITLE', 'Affinity', 'en_US'),
('LOC_PEDIA_CONCEPTS_PAGE_ALIGNMENT_CHAPTER_CONTENT_TITLE', 'Alignment', 'en_US'),
('LOC_PEDIA_CONCEPTS_PAGE_ARMAGEDDON_COUNTER_CHAPTER_CONTENT_TITLE', 'Armageddon Counter', 'en_US'),
('LOC_PEDIA_CONCEPTS_PAGE_CRIME_RATE_CHAPTER_CONTENT_TITLE', 'Crime Rate', 'en_US'),
('LOC_PEDIA_CONCEPTS_PAGE_DEFENSIVE_STRIKE_CHAPTER_CONTENT_TITLE', 'Defensive Strike', 'en_US'),
